    <script>
        // Ambil semua elemen SVG dan Keterangan
        var svgKotakList = document.querySelectorAll('.svg-kotak');
        var keterangan = document.getElementById('keterangan');

        // Tambahkan event listener untuk setiap SVG kotak
        svgKotakList.forEach(function(svgKotak) {
            svgKotak.addEventListener('mouseover', function() {
                // Ambil keterangan dari atribut data-keterangan
                var keteranganText = svgKotak.getAttribute('data-keterangan');

                // Set teks keterangan
                keterangan.innerText = keteranganText;

                // Set display keterangan menjadi block saat mouse hover
                keterangan.style.display = 'block';
            });

            // Tambahkan event listener untuk setiap SVG kotak
            svgKotak.addEventListener('mouseout', function() {
                // Set display keterangan menjadi none saat mouse keluar
                keterangan.style.display = 'none';
            });
        });
    </script>

